import React,{useState} from 'react'

export default function TextForm(props) {
    const [text,setText] = useState('');
    const handleOnchng = (event)=>{
        setText(event.target.value);
    }
    const handleTogOnclk = ()=>{
        if(text.match(/[a-z]/)){
            let newText = text.toUpperCase();
            setText(newText);
            props.showAlert('Converted to UpperCase', 'success');
        }
        else{
            let newText = text.toLowerCase();
            setText(newText);
            props.showAlert('Converted to LowerCase', 'success');
        }

    }
    const handleLowOnclk = ()=>{
        let newText = text.toLowerCase();
        setText(newText);
        props.showAlert('Converted to LowerCase', 'success');
    }
    const handleUpOnclk = ()=>{
        let newText = text.toUpperCase();
        setText(newText);
        props.showAlert('Converted to UpperCase', 'success');

    }
    const handleClrOnclk = ()=>{
        setText("");
        props.showAlert('Converted to LowerCase', 'success');

    }
    const handleCpyOnclk = ()=>{
        navigator.clipboard.writeText(text);
    }
    const handleRemOnclk = ()=>{
        if(text.match(/[a-z]/)){
            let newText = text.toUpperCase();
            setText(newText);
            props.showAlert('Converted to UpperCase', 'success');
        }
        else{
            let newText = text.toLowerCase();
            setText(newText);
            props.showAlert('Converted to LowerCase', 'success');
        }

    }
  return (
    <>
        <div className="mb-3" style = {{color: props.mode==='light'?'black':'white'}}>
            <h1>{props.heading}</h1>
            <textarea className="form-control" onChange={handleOnchng} style = {{backgroundColor: props.mode==='dark'?'grey':'white',
            color: props.mode==='light'?'black':'white'}} id="textArea" value={text}rows="8"></textarea>
        </div>
        <button className="btn btn-primary" onClick={handleUpOnclk}>Upper Case</button>
        <button className="btn btn-primary" onClick={handleLowOnclk}>Lower Case</button>
        <button className="btn btn-primary" onClick={handleClrOnclk}>Clear Text</button>
        <button className="btn btn-primary" onClick={handleCpyOnclk}>Copy Text</button>
        <button className="btn btn-primary" onClick={handleRemOnclk}>Remove extra spaces</button>
        <button className="btn btn-primary" onClick={handleTogOnclk}>Toggle Case</button>

        <div className="container my-3 my-1" style = {{color: props.mode==='light'?'black':'white'}}>
            <h2>Text Summary</h2>
            <p>{text.split(" ").filter((element) =>{return  element.length!==0}).length} words and {text.length} characters</p>
            <p>{0.08*text.split(" ").filter((element) =>{return  element.length!==0}).length} Minutes Read</p>
            <h2>Preview</h2>
            <p>{text.length>0?text:'Enter text in the textBox to preview here'}</p>
        </div>
    </>
  )
}
